mapper.update:
update tim_member set 
MEMBER_DATE = member_date

	,MEMBER_PW = #{memberPw}


	,MEMBER_NICK = #{memberNick}


	,MEMBER_NAME = #{memberName}


	,MEMBER_PHONE = #{memberPhone}


	,MEMBER_ADDRESS = #{memberAddress}

where member_id = #{memberId}

mapper.select:
select *
from tim_member
where MEMBER_ID = #{id}
and MEMBER_PW = #{pw}
