mapper.update:
UPDATE tim_goodsinfo
    SET goods_mdate = sysdate

        , goods_name = #{goodsName}


        , goods_price = #{goodsPrice}


        , goods_inven = #{goodsInven}


        , goods_state = #{goodsState}


        , goods_catename = #{goodsCatename}

    WHERE seq_goods = #{seqGoods}
